mapper.select:
select
,u.username
from rpc_log l
left join sys_user u on l.create_user=u.uuid


		and l.call_time >=#{f.callTimeFrom}


		and l.call_time <= #{f.callTimeEnd}


		and l.module =#{f.module}


		and l.todo=#{f.todo}


		and l.ip like concat('%',#{f.ip},'%')


		and l.result #{f.result}


		and l.error_msg like CONCAT('%',#{f.errorMsg},'%')


		and l.create_user in (select uuid from sys_user where username like CONCAT('%',#{f.userName},'%'))


		and l.type=#{f.type}


		and ( l.ip like concat('%',#{f.ipOrUserName},'%') or l.create_user in (select uuid from sys_user where username like CONCAT('%',#{f.ipOrUserName},'%')) )


order by l.call_time desc ,l.id desc

	LIMIT #{beginCount},#{pageRows}

select
count(l.id)
from rpc_log l
left join sys_user u on l.create_user=u.uuid


		and l.call_time >=#{f.callTimeFrom}


		and l.call_time <= #{f.callTimeEnd}


		and l.module =#{f.module}


		and l.todo=#{f.todo}


		and l.ip like concat('%',#{f.ip},'%')


		and l.result #{f.result}


		and l.error_msg like CONCAT('%',#{f.errorMsg},'%')


		and l.create_user in (select uuid from sys_user where username like CONCAT('%',#{f.userName},'%'))


		and l.type=#{f.type}


		and ( l.ip like concat('%',#{f.ipOrUserName},'%') or l.create_user in (select uuid from sys_user where username like CONCAT('%',#{f.ipOrUserName},'%')) )

select
,u.username
from rpc_log l
left join sys_user u on l.create_user=u.uuid, (select @i:=0)t


		and l.call_time >=#{callTimeFrom}


		and l.call_time <= #{callTimeEnd}


		and l.module =#{module}


		and l.todo=#{todo}


		and l.ip =#{ip}


		and l.result #{result}


		and l.error_msg like CONCAT('%',#{errorMsg},'%')


		and l.create_user in (select uuid from sys_user where username like CONCAT('%',#{userName},'%'))


		and l.type=#{type}


order by call_time desc ,id desc

